import { Component, OnInit, HostListener } from '@angular/core';

@Component({
  selector: 'app-about',
  templateUrl: './about-us.component.html',
  styleUrls: ['./about-us.component.css'],
})
export class AboutUsComponent implements OnInit {
  stats = [
    { label: 'Projects', value: 70, icon: 'checklist' },
    { label: 'Employees', value: 140, icon: 'group' },
    { label: 'Customers', value: 340, icon: 'groups' },
  ];

  private statsAnimated = false;

  constructor() {}

  ngOnInit(): void {}

  @HostListener('window:scroll', [])
  onWindowScroll() {
    const statisticsSection = document.querySelector('.statistics');
    if (statisticsSection) {
      const sectionTop = statisticsSection.getBoundingClientRect().top;
      const windowHeight = window.innerHeight;

      if (sectionTop < windowHeight && !this.statsAnimated) {
        this.animateStats();
        this.statsAnimated = true;
      }
    }
  }

  animateStats(): void {
    this.stats.forEach((stat, index) => {
      const element = document.getElementById(`stat-${index}`);
      if (element) {
        let count = 0;
        const target = stat.value;
        const increment = Math.ceil(target / 50);
        const interval = setInterval(() => {
          count += increment;
          if (count >= target) {
            count = target;
            clearInterval(interval);
          }
          element.innerText = count.toString();
        }, 50);
      }
    });
  }
}
<!-- Hero Section -->
<div class="row">
  <div class="col-md-12">
    <section
      class="header img-fluid"
      style="background-image: url('assets/imgs/bw.jpg')"
    >
      <div class="overlay"></div>
      <div class="header-content">
        <h1>About Us</h1>
        <h3>
          <a routerLink="/home" style="color: inherit; text-decoration: none"
            >Home</a
          >
          - <span> About Us </span>
        </h3>
      </div>
    </section>
  </div>
</div>

<!-- Who We Are Section -->
<div class="about-inchaat">
  <div class="container">
    <div class="row">
      <div class="col-md-6 mb-30">
        <div class="title">
          <h1>Who We Are</h1>
        </div>
        <div class="about-inchaat-content">
          <p>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aperiam,
            quis quo neque obcaecati cumque sunt magni a exercitationem.
            Distinctio iure quidem, necessitatibus odit laboriosam nostrum! Modi
            dicta odio quisquam debitis. Lorem ipsum dolor sit, amet consectetur
            adipisicing elit. Laudantium asperiores autem sed unde eveniet
            dolorem itaque ea ex aut ipsa, praesentium veritatis. Sequi iusto ex
            exercitationem, ipsam inventore corrupti architecto.
          </p>
        </div>
      </div>
      <div class="about-inchaat-img col-md-6">
        <img src="assets/imgs/s1.jpg" alt="About Us Image" />
      </div>
    </div>
  </div>
</div>

<!-- Postcard -->
<div class="postcard-section">
  <div class="container">
    <article class="postcard light blue">
      <img
        class="postcard__img"
        src="https://picsum.photos/1000/1000"
        alt="Image Title"
      />
      <div class="postcard__text t-dark">
        <h1 class="postcard__title blue">Our Vision</h1>
        <div class="postcard__bar"></div>
        <div class="postcard__preview-txt">
          Lorem ipsum dolor sit amet consectetur adipisicing elit. A ipsam sed
          ab, eum quo officiis excepturi beatae totam itaque tenetur facere
          accusamus odit corporis ex exercitationem perspiciatis consequuntur
          dolores numquam.
        </div>
      </div>
    </article>
  </div>
</div>

<!-- Statistics Section -->
<div class="statistics" #statisticsSection>
  <div class="row justify-content-center">
    <div
      class="stat-content col-sm-2"
      *ngFor="let stat of stats; let i = index"
    >
      <div class="icon-container">
        <span class="material-symbols-outlined">{{ stat.icon }}</span>
      </div>
      <h1 [attr.id]="'stat-' + i">0</h1>
      <p class="subtitle">{{ stat.label }}</p>
    </div>
  </div>
</div>
