import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-project-detail',
  templateUrl: './project-detail.component.html',
  styleUrls: ['./project-detail.component.css'],
})
export class ProjectDetailComponent implements OnInit {
  project: any;
  selectedImage: string = '';

  constructor(private route: ActivatedRoute, private router: Router) {}

  ngOnInit() {
    const projectId = this.route.snapshot.params['id'];
    // Mock project data (Replace with API call)
    this.project = {
      id: 1,
      title: 'Al Shaya Tower',
      images: [
        'assets/imgs/projects/alshaya1.jpeg',
        'assets/imgs/projects/alshaya2.jpeg',
        'assets/imgs/projects/alshaya3.jpeg',
        'assets/imgs/projects/alshaya4.jpeg',
        'assets/imgs/projects/alshaya5.jpeg',
        'assets/imgs/projects/alshaya6.jpeg',
        'assets/imgs/projects/alshaya7.jpeg',
        'assets/imgs/projects/alshaya8.jpeg',
        'assets/imgs/projects/alshaya9.jpeg',
        'assets/imgs/projects/alshaya10.jpeg',
        'assets/imgs/projects/alshaya11.jpeg',
        'assets/imgs/projects/alshaya12.jpeg',
        'assets/imgs/projects/alshaya13.jpeg',
        'assets/imgs/projects/alshaya14.jpeg',
        // Add other images
      ],
      location: 'Dubai, UAE',
      description: 'A state-of-the-art tower located in Dubai.',
      timeline: '2023 - 2024',
    };
    this.selectedImage = this.project.images[0];
  }

  selectImage(img: string) {
    this.selectedImage = img;
  }

  goBack() {
    this.router.navigate(['./projects']);
  }
}
<div class="project-detail container py-5">
  <button class="btn btn-secondary mb-4" (click)="goBack()">← Back</button>
  <h2 class="text-center mb-4">{{ project.title }}</h2>

  <div class="row">
    <!-- Image Gallery -->
    <div class="col-md-8">
      <div class="gallery">
        <img
          [src]="selectedImage"
          alt="{{ project.title }}"
          class="main-img mb-3"
        />
        <div class="thumbnails">
          <img
            *ngFor="let img of project.images"
            [src]="img"
            [alt]="project.title"
            class="thumbnail"
            [class.active]="img === selectedImage"
            (click)="selectImage(img)"
          />
        </div>
      </div>
    </div>

    <!-- Project Info -->
    <div class="col-md-4">
      <h4>Project Details</h4>
      <p>{{ project.description }}</p>
      <ul>
        <li><strong>Location:</strong> {{ project.location }}</li>
        <li><strong>Timeline:</strong> {{ project.timeline }}</li>
      </ul>
    </div>
  </div>
</div>
